mapper.update:
UPDATE KSS_SOFT

	intervaltime = #{intervaltime},

		privkey = #{privkey}


where id=#{id}

update kss_soft

	status = #{status}

where id in

	#{id}

mapper.select:
select * from kss_soft 
where id in

	#{id}

mapper.delete:
delete from kss_soft
where id in

	#{id}

mapper.sql:


		softcode = #{softcode}


		and softname = #{softname}


		and status in

			#{s}



		and id in
		(
		select distinct k.softid from kss_power p 
		inner join kss_keyset k on p.keysetid=k.id
		where p.adminid=#{adminid}
		)


order by id
